def determine_tokens(addresses, chains):
    """
    Given at least 1 address and 1 chain, check which of these addresses is a token on one of the chains
    :param addresses:
    :param chains:
    :return: list[tuple[str, str]] -> address-chain-pairs
    """

    results_found = []

    for chain in chains:
        dbt_config = get_dbt_config(chain)
        helper = dexbuytools.helpers.get_helper(chain, dbt_config)

        for address in addresses:
            try:
                pair_contract = helper.w3.eth.contract(
                    abi=UNISWAP_V2_PAIR_ABI,
                    address=Web3.toChecksumAddress(address)
                )
                token0 = pair_contract.functions.token0().call().lower()
                token1 = pair_contract.functions.token1().call().lower()
                main_token = helper.chain_data['MAIN_TOKEN_ADDRESS'].lower()

                if main_token == token0:
                    results_found.append((token1, chain))

                if main_token == token1:
                    results_found.append((token0, chain))

            except Exception as e:
                try:
                    erc20_contract = helper.w3.eth.contract(
                        abi=ERC20_ABI,
                        address=Web3.toChecksumAddress(address)
                    )
                    #testing if the erc20-read-functions exist. Better way might be to check for function signatures
                    erc20_contract.functions.balanceOf('0x0000000000000000000000000000000000000000').call()
                    erc20_contract.functions.totalSupply().call()
                    erc20_contract.functions.name().call()
                    erc20_contract.functions.allowance('0x0000000000000000000000000000000000000000',
                                                       '0x0000000000000000000000000000000000000000').call()

                    results_found.append((address, chain))

                except Exception as e:
                    continue

    results_found = list(map(lambda x: (x[0].lower(), x[1]), results_found))
    return list(dict.fromkeys(results_found)) #remove duplicates

def buy(address_chain_pairs):
    """

    :param address_chain_pairs: result of :func:`chain_utils.determine_tokens`
    :return:
    """

    for address, chain in address_chain_pairs:

        dbt_config = get_dbt_config(chain)

        helper = dexbuytools.helpers.get_helper(chain, dbt_config)
        receipt = helper.buy_instantly(address)

        notifications.send_telegram_message(f"TTS: bought '''{address}''' on {chain}")


file_dir = os.path.dirname(__file__)

def get_dbt_config(chain):
    buy_params_path = f'{file_dir}/../dbt_config/{chain}_buy_params.yml'
    wallet_data_path = f'{file_dir}/../dbt_config/{chain}_wallet_data.yml'
    general_params_path = f'{file_dir}/../dbt_config/general_params.yml'
    return dexbuytools.config.get_config(buy_params_path, wallet_data_path, general_params_path)
